namespace CleanArchitecture.Blazor.Application.Features.MenuSectionSubItems.Specifications;
#nullable disable warnings
/// <summary>
/// Specification class for advanced filtering of MenuSectionSubItems.
/// </summary>
public class MenuSectionSubItemAdvancedSpecification : Specification<MenuSectionSubItem>
{
    public MenuSectionSubItemAdvancedSpecification(MenuSectionSubItemAdvancedFilter filter)
    {
        DateTime today = DateTime.UtcNow;
        var todayrange = today.GetDateRange(MenuSectionSubItemListView.TODAY.ToString(), filter.LocalTimezoneOffset);
        var last30daysrange = today.GetDateRange(MenuSectionSubItemListView.LAST_30_DAYS.ToString(),filter.LocalTimezoneOffset);

        Query.Where(q => q.Title != null)
             .Where(filter.Keyword,!string.IsNullOrEmpty(filter.Keyword))
             .Where(q => q.CreatedBy == filter.CurrentUser.UserId, filter.ListView == MenuSectionSubItemListView.My && filter.CurrentUser is not null)
             .Where(x => x.Created >= todayrange.Start && x.Created < todayrange.End.AddDays(1), filter.ListView == MenuSectionSubItemListView.TODAY)
             .Where(x => x.Created >= last30daysrange.Start, filter.ListView == MenuSectionSubItemListView.LAST_30_DAYS);
       
    }
}
